document.addEventListener('DOMContentLoaded', () => {
    let oldScroll = 0;
    let scrollDirection = 1;
    window.addEventListener('scroll', () => {
        scrollDirection = window.scrollY > oldScroll ? 1 : -1;
        console.log
        if (window.scrollY > 100) {
            document.getElementById('pf_nav').classList.add('navbar-scrolled');
            document.getElementById('pf_nav').classList.remove('navbar-scroll');

        } else {
            document.getElementById('pf_nav').classList.add('navbar-scroll');
            document.getElementById('pf_nav').classList.remove('navbar-scrolled');
        }
        // if (scrollDirection === 1) {
        //     document.querySelectorAll('.aod').forEach(element => setAnimation(element));
        // }

    });

    // setAnimation = (element) => {
    //     if (isElementVisibleInViewportCenter(element)) {
    //         element.classList.add('animate__animated', 'animate__fadeInUp', 'visible');
    //         element.classList.remove('invisible')
    //     }
    // }

    isElementVisibleInViewportCenter = (element) => {
        const { top, bottom } = element.getBoundingClientRect();
        const vHeight = (window.innerHeight || document.documentElement.clientHeight);
        return (
            (top > 0 || bottom > 0) &&
            (top) < vHeight
        );
    }
})