document.addEventListener('DOMContentLoaded', () => {
    let oldScroll = 0;
    let scrollDirection = 1;
    document.getElementById('currentYear').innerText = new Date().getFullYear();
    document.querySelectorAll('.nav-link').forEach((element) => {
        element.addEventListener('click', (event) => {
            document.querySelectorAll('.nav-link').forEach((elementItem) => elementItem.classList.remove('active'));
            event.target.classList.add('active');
        });
    });
    window.addEventListener('scroll', () => {
        scrollDirection = window.scrollY > oldScroll ? 1 : -1;
        if (window.scrollY > 100) {
            document.getElementById('pf_nav').classList.add('navbar-scrolled');
            document.getElementById('pf_nav').classList.remove('navbar-scroll');

        } else {
            document.getElementById('pf_nav').classList.add('navbar-scroll');
            document.getElementById('pf_nav').classList.remove('navbar-scrolled');
        }

    });
    document.getElementById('toggleShow').addEventListener('click', (event) => {
        document.querySelectorAll('.pf_more').forEach(element => {
            if (element.classList.contains('d-block')) {
                element.classList.remove('d-block')
            } else {
                element.classList.add('d-block')
            }
        });
        event.target.innerText = event.target.innerText === 'Show Less' ? 'Show More' : 'Show Less'
    });
    isElementVisibleInViewportCenter = (element) => {
        const { top, bottom } = element.getBoundingClientRect();
        const vHeight = (window.innerHeight || document.documentElement.clientHeight);
        return (
            (top > 0 || bottom > 0) &&
            (top) < vHeight
        );
    }
})